import React, { Component } from 'react'
import axios from "axios";
import {Link} from "react-router-dom"
import Heading from "../../../components/heading/Heading"
export default class Signup extends Component {
    constructor(props){
        super(props);
        this.state={
          username:"",
          firstname:"",
          lastname:"",
          email:"",
          password:"",
          error:""
        }
      }
      handleChange=(e)=>{
        e.preventDefault();
        this.setState({
          [e.target.name]:e.target.value
        })
      }

      submit=(e)=>{
        e.preventDefault();

        axios.post("http://localhost:3002/auth/signup",this.state).then((response)=>{
          if(!response.data._id){
            this.setState({
              error:"User already exist"
            })
          }else{
            const obj={
              data:response.data,
            }
            localStorage.setItem("id",obj)
            this.setState({
              error:""
            })
          }
        })

      }
      render(){
        return (
          <div>
            <meta charSet="utf-8" />
            {/* <title>Create An Account</title> */}
            <link href="css/bootstrap.css" rel="stylesheet" type="text/css" />
            <link href="css/bootstrap-responsive.css" rel="stylesheet" type="text/css" />


                <div className="content_rgt">
                  <div className="register_sec">
                  <Heading content="Create an account" size="34px"/>
                    <form onSubmit={this.submit}>
                    <ul>
                      <li><span>Username</span><input type="text" name="username"onChange={this.handleChange} placeholder="Enter your username" required/></li>
                      <li><span>Password</span><input type="password" name="password"onChange={this.handleChange} placeholder="Enter your password" required /></li>
                      <li><span>Email</span><input type="email" name="email"onChange={this.handleChange} placeholder="Enter your email" required /></li>
                      <li><span>First Name</span><input type="text" name="firstname"onChange={this.handleChange} placeholder="Enter your first name" required /></li>
                      <li><span>Last Name</span><input type="text" name="lastname"onChange={this.handleChange} placeholder="Enter your last name" required/></li>
                      <li><input type="checkbox" required/>I agree to Term &amp; Conditions</li>
                      <p style={{color:"red"}}>{this.state.error}</p>
                      <li><input type="submit"/></li>
                    </ul>
                    </form>
                    <div className="addtnal_acnt">I already have an account.<a ><Link to="/login">Login My Account !</Link></a></div>
                  </div>
                </div>
             </div>

)}}
