import React, { Component } from 'react'
import axios from "axios"
export default class Register extends Component {
    constructor(props){
        super(props);
        this.state={
          username:"",
          firstname:"",
          lastname:"",
          email:"",
          password:"",
          error:""
        }
      }
      handleChange=(e)=>{
        e.preventDefault();
        this.setState({
          [e.target.name]:e.target.value
        })
      }
      submit1=(e)=>{
        console.log(new Date(),new Date().getTime());
      }
      submit=(e)=>{
        e.preventDefault();

        axios.post("http://localhost:3002/auth/signup",this.state).then((response)=>{
          if(!response.data._id){
            this.setState({
              error:"User already exist"
            })
          }else{
            const obj={
              data:response.data,
            }
            localStorage.setItem("id",obj)
            this.setState({
              error:""
            })
          }
        })

      }
      render(){
        return (
          <div>
            <meta charSet="utf-8" />
            <title>Create An Account</title>
            <link href="css/bootstrap.css" rel="stylesheet" type="text/css" />
            <link href="css/bootstrap-responsive.css" rel="stylesheet" type="text/css" />

            <div className="container">
              <div className="content">
                <div className="content_rgt">
                  <div className="register_sec">
                    <h1>Create An Account</h1>
                    <form onSubmit={this.submit}>
                    <ul>
                      <li><span>Username</span><input type="text" name="username"onChange={this.handleChange} placeholder="Enter your username" required/></li>
                      <li><span>Password</span><input type="password" name="password"onChange={this.handleChange} placeholder="Enter your password" required /></li>
                      <li><span>Email</span><input type="email" name="email"onChange={this.handleChange} placeholder="Enter your email" required /></li>
                      <li><span>First Name</span><input type="text" name="firstname"onChange={this.handleChange} placeholder="Enter your first name" required /></li>
                      <li><span>Last Name</span><input type="text" name="lastname"onChange={this.handleChange} placeholder="Enter your last name" required/></li>
                      <li><input type="checkbox" required/>I agree to Term &amp; Conditions</li>
                      <p style={{color:"red"}}>{this.state.error}</p>
                      <li><input type="submit"/></li>
                      <button onClick={this.submit1}>get date</button>
                    </ul>
                    </form>
                    <div className="addtnal_acnt">I already have an account.<a href>Login My Account !</a></div>
                  </div>
                </div>
                <div className="content_lft">
                  <h1>Welcome from PPL!</h1>
                  <p className="discrptn">There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text. </p>
                  <img src="./images/img_9.png" alt="" /> </div>
              </div>
            </div>
            <div className="clear" />
            <div className="footr">
              <div className="footr_lft">
                <div className="footer_div1">Copyright Â© Pet-Socail 2014 All Rights Reserved</div>
                <div className="footer_div2"><a href="#">Privacy Policy </a>| <a href="#"> Terms &amp; Conditions</a></div>
              </div>
              <div className="footr_rgt">
                <ul>
                  <li><a href="#"><img src="./images/social_1.png" /></a></li>
                  <li><a href="#"><img src="./images/social_2.png" /></a></li>
                  <li><a href="#"><img src="./images/social_3.png" /></a></li>
                  <li><a href="#"><img src="./images/social_4.png" /></a></li>
                </ul>
              </div>
            </div>
          </div>
        );
      }
    };
