    constructor(props){
        super(props);
        this.state={
          username:"",
          firstname:"",
          lastname:"",
          email:"",
          password:"",
          error:""
        }
      }
      handleChange=(e)=>{
        e.preventDefault();
        this.setState({
          [e.target.name]:e.target.value
        })
      }

      submit=(e)=>{
        e.preventDefault();

        axios.post("http://localhost:3002/auth/signup",this.state).then((response)=>{
          if(!response.data._id){
            this.setState({
              error:"User already exist"
            })
          }else{

            localStorage.setItem("id",response.data._id)
            this.setState({
              error:""
            })
          }
        })

      }
      render(){
        return (
          <div>
            <meta charSet="utf-8" /> 
